d=""

def findChunk(d, partName, chunkSize)
    chars = []                          #will be chunk-to-inspect
    for pos in 1..d.length              #move through input
        chars.push(d[pos])              #add to chunk
        if chars.length > chunkSize     #if over chunk size
            chars.shift()               #    shift (remove left char)
        end
        if chars.uniq.length == chunkSize   #and see if size of the  unique characters in the chunk is the same as the chunk size
            break                           #done check loop
        end
    end
    puts "PART #{partName}, pos: #{pos+1}"  #0 index arrah need a boost
end

